function handleEnableDisable(t){let e=parseInt($(`#id_qty_${t}`).val()),n=e<2,l=e>98;$(`#decrement-qty_${t}`).prop("disabled",n),$(`#increment-qty_${t}`).prop("disabled",l)}let allQtyInputs=$(".qty_input");for(let t=0;t<allQtyInputs.length;t++){let e=$(allQtyInputs[t]).data("item_id");handleEnableDisable(e)}$(".qty_input").on("change",function(){let t=$(this).data("item_id");handleEnableDisable(t)}),$(".increment-qty").on("click",function(t){t.preventDefault();let e=$(this).closest(".input-group").find(".qty_input")[0],n=parseInt($(e).val());$(e).val(n+1);let l=$(this).data("item_id");handleEnableDisable(l)}),$(".decrement-qty").on("click",function(t){t.preventDefault();let e=$(this).closest(".input-group").find(".qty_input")[0],n=parseInt($(e).val());$(e).val(n-1);let l=$(this).data("item_id");handleEnableDisable(l)});